import { Component, OnInit } from '@angular/core';
import { User } from 'firebase/auth';
import { UserService } from 'src/app/shared/user.service';

@Component({
  selector: 'app-perfil',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css'],
})
export class PerfilComponent implements OnInit {
  user: User | null | undefined;
  novoNome: string = '';
  novaFotoURL: string = '';

  constructor(private userService: UserService) {}

  ngOnInit(): void {
    this.userService.getCurrentUser().then((user) => {
      this.user = user;
    });
  }

  atualizarPerfil(): void {
    this.userService
      .updateUserInfo(this.novoNome, this.novaFotoURL)
      .then(() => {
        // Atualização bem-sucedida
        alert('Perfil atualizado com sucesso.');
      })
      .catch((error) => {
        // Lidar com erros
        console.error('Erro ao atualizar o perfil:', error);
      });
  }
}
<!DOCTYPE html>
<html lang="pt-br">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Login</title>
    <link rel="login.component.css" href="src/app/component/login/login.component.css">
</head>
<body class="login">
    <h1>Perfil</h1>
<form> 
<div class="form-group">
    <input type="email" id="email" name="email" [(ngModel)]="email" placeholder="Seu email" required>
</div>
<div class="form-group">
    <input type="password" id="password" name="password" [(ngModel)]="password" placeholder="Sua senha" required>
</div>
</form>

</body>
</html>
