import { Component, OnInit } from '@angular/core';
import { AuthService } from 'src/app/shared/auth.service';
import { getAuth, 
  GoogleAuthProvider, 
  Auth, 
  signInWithPopup, 
  UserCredential, 
  setPersistence,
  browserLocalPersistence,
  browserSessionPersistence,
  inMemoryPersistence } from 'firebase/auth';



@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent implements OnInit {
  email : string = '';
  password : string = '';


  constructor(private auth : AuthService){
    const firebaseAuth = getAuth()

    setPersistence(firebaseAuth, Persistence.LOCAL)
    .then(() => {

    })
    .catch((error) => {
      console.log(error);
    });
  }

  ngOnInit(): void {
  }


 login() {
  if(this.email == '') {
    alert('Por favor insira seu email');
    return;
  }

  if(this.password == '') {
    alert('Por favor insira sua senha');
    return;
  }

  this.auth.login(this.email, this.password);

  this.email = '';
  this.password = '';
 }

}
<!DOCTYPE html>
<html lang="pt-br">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Login</title>
    <link rel="login.component.css" href="src/app/component/login/login.component.css">
</head>
<body class="login">
    <h1>Fazer login</h1>
<form> 
<div class="form-group">
    <input type="email" id="email" name="email" [(ngModel)]="email" placeholder="Seu email" required>
</div>
<div class="form-group">
    <input type="password" id="password" name="password" [(ngModel)]="password" placeholder="Sua senha" required>
</div>
<button type="submit" (click)="login()">Entrar</button>
<a type="submit" href="register()">Cadastre-se</a>
<a href="/forgot-password">Esqueci a senha.</a>
</form>

</body>
</html>
