import { Component, OnInit } from '@angular/core';
import { AuthService } from 'src/app/shared/auth.service';


@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent implements OnInit {
  email : string = '';
  password : string = '';
  Auth: any;


  constructor(private auth : AuthService){}
  ngOnInit(): void {

  }


 login() {
  if(this.email == '') {
    alert('Por favor insira seu email');
    return;
  }

  if(this.password == '') {
    alert('Por favor insira sua senha');
    return;
  }

  this.Auth.login(this.email, this.password);

  this.email = '';
  this.password = '';
 }

}
<!DOCTYPE html>
<html lang="pt-br">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Login</title>
</head>
<body>

<form> 
<div class="form-group">
    <label for="email">Email:</label>
    <input type="email" id="email" name="email" [(ngModel)]="email" placeholder="Seu email" required>
</div>
<div class="form-group">
    <label form="password">Senha:</label>
    <input type="password" id="password" name="password" [(ngModel)]="password" placeholder="Sua senha" required>
</div>
<button type="submit" (click)="login()" >Entrar</button>
</form>

</body>
</html>
