import { Component, OnInit } from '@angular/core';
import { User } from 'firebase/auth';
import { UserService } from "src/app/shared/UserService";

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class ProfileComponent implements OnInit {
  user: User | null | undefined;

  constructor(private userService: UserService) { }
  ngOnInit(): void { 
    this.userService.getCurrentUser().then((user) => {
      this.user = user;
    });
  }

  // função que atualiza as informações do usuario
  updatePerfil(displayName: string, photoURL: string): void {
    this.userService.updateUserInfo(displayName, photoURL).then(() => {
      // atualização ocorreu
    }).catch((error) => {
      // mensagem erro
    });
  }
}
