import { Component, inject } from '@angular/core';
import { Firestore, collection, collectionData } from '@angular/fire/firestore';
import { Observable } from 'rxjs';
import { environment } from 'src/environments/environment';
import { AngularFireAuth } from '@angular/fire/compat/auth';
import { AuthService } from 'src/app/shared/auth.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'projeto-integrado';
  firestore: Firestore = inject(Firestore);
  items$: Observable<any[]>;
  currentUser: any; // Adicione a propriedade currentUser

  constructor(private auth: AngularFireAuth) { // Injete o serviço AngularFireAuth no construtor
    const aCollection = collection(this.firestore, 'items');
    this.items$ = collectionData(aCollection);
  }

  logInUser(): void {
    this.auth.setPersistence('local').then(() => {
      this.auth.signInWithEmailAndPassword(environment.email, environment.password)
        .then(userCredential => {
          this.currentUser = userCredential.user;
          // Faça algo com this.currentUser após o login bem-sucedido
        })
        .catch(error => {
          console.error('Erro ao fazer login:', error);
          // Lide com erros de autenticação aqui
        });
    });
  }
}


